import React, { Component } from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import { Fade } from "react-awesome-reveal";
import '../page_css/solution.css';
import back from '../../components/static/back.png';
import arch from '../../components/architecture/tourism.png';
import arch2 from '../../components/architecture/tourism2.png';
import desc from '../../components/architecture/tourism-desc.png';
import desc2 from '../../components/architecture/tourism-desc2.png';
import desc3 from '../../components/architecture/tourism-desc3.png';
import mp4 from '../../components/gif/exampleMP4.mp4';
import play from '../../components/gif/play-btn.png';
import pause from '../../components/gif/pause-btn.png';


class Tourism extends Component {
    constructor(props){
        super(props);
        this.state = {
            play: true
        }
    }

    getVideo = e => {
        this.video = e
    }

    playVideo = () => {
        this.video.play();
        this.setState({
            play: !this.state.play
        });
    }
    pauseVideo = () => {
        this.video.pause();
        this.setState({
            play: !this.state.play
        });
    }

    render() {
        return(
            <div className='solution'>
               <Container>
                    <Fade>
                    <Row id='description'>
                        <Col>
                            <p id='description-title'><strong>Tourism Industry Analytics</strong><Link to='/signal'><img src={back} alt='img' id='back-icon'/></Link></p>
                        </Col>
                    </Row>
                    <Row>
                            <Col>
                                <Tabs style={{ borderBottom: '' }} defaultActiveKey="description" transition={false}>
                                    <Tab id='tab-btn' eventKey="description" title="Description">
                                        <div className='desc' style={{height:'55vh'}}>
                                            <img alt='desc' src={desc} style={{width:'100%'}}/>
                                            <img alt='desc' src={desc2} style={{width:'100%', paddingLeft:'5%', paddingRight:'5%'}}/>
                                            <img alt='desc' src={desc3} style={{width:'100%'}}/>

                                        </div>
                                    </Tab>
                                    <Tab id='tab-btn' eventKey="architecture" title="Architecture">
                                        <div className='arch' style={{height:'55vh'}}>
                                            <img style={{width:'100%', marginTop:'40px'}} alt='architecture' src={arch2} />
                                            <img style={{width:'100%', marginTop:'40px'}} alt='architecture' src={arch} />
                                        </div>
                                    </Tab>
                                    <Tab id='tab-btn' eventKey="gif" title="Video">
                                        {/* <img src={gif} alt="gif" id='gif' /> */}
                                        <video style={{width: '100%'}} ref={this.getVideo} src={mp4} type="video/mp4"/>
                                        <img className='play' src={this.state.play? play : pause} alt="play" onClick={this.state.play? this.playVideo : this.pauseVideo} />
                                    </Tab>
                                    {/* <Tab id='tab-btn' eventKey="live_demo" title="Live Demo"></Tab> */}
                                </Tabs>
                            </Col>
                        </Row>
                    </Fade>
                </Container>
            </div>
        );
    }
}

export default Tourism;



