import { makeStyles } from '@material-ui/core/styles';
import React from 'react';
import annotationlist from '../../static/annotationlist.json';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Checkbox from '@material-ui/core/Checkbox';
import clsx from 'clsx';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';
import Typography from '@material-ui/core/Typography';

const qalist = annotationlist.annotatonlist;

const useStyles = makeStyles((theme) => ({
    root: {
        height: '100%',
    },
    list: {
        height: '85%',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'auto',
        padding: theme.spacing(2),
    },
    listHead: {
        height: '7%',
        textAlign: 'left',
        paddingLeft: theme.spacing(2),
        paddingTop: theme.spacing(1),
        fontSize: '25px',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    highlight: {
        backgroundColor: 'rgba(255, 255, 255, 0.85)',
    },
}));

function handleDelete() {
    alert("delete");
}

export default function QAList({ fileId }) {
    const classes = useStyles();
    const [selected, setSelected] = React.useState([]);
    const numSelected = selected.length;
    // const numSelected = 3;

    const isSelected = (id) => selected.indexOf(id) !== -1;


    return (
        <div className={classes.root} id='qalist'>
            <div className={clsx(classes.listHead, {
                [classes.highlight]: numSelected > 0,
            })}>
                {numSelected > 0 ? (
                    <Typography color="inherit" variant="subtitle1" component="div">
                        {numSelected} selected
                    </Typography>
                ) : (
                        <Typography variant="h6" id="tableTitle" component="div">
                            Results
                        </Typography>
                )}

                {numSelected > 0 ? (
                    <Tooltip title="Delete">
                        <IconButton aria-label="delete" onClick={handleDelete}>
                            <DeleteIcon />
                        </IconButton>
                    </Tooltip>
                ) : (
                        null
                )}
            </div>

            <Divider />

            <div className={classes.list}>
                {
                    qalist.map((qa) => {
                        const isItemSelected = isSelected(qa.id);
                        if (qa.fileID === fileId)
                            return (<FormControl>
                                <div style={{ marginBottom: '5%', display: 'flex', flexDirection: "row" }}>
                                    <Checkbox checked={isItemSelected}/>
                                    <TextField
                                        label={qa.resultKey}
                                        defaultValue={qa.resultValue}
                                        variant="outlined"
                                        fullWidth
                                        InputProps={{
                                            readOnly: true,
                                        }}
                                    />
                                </div>
                            </FormControl>)
                        else
                            return null
                    })
                }
            </div>
            {/* <div className={classes.btn}><Button variant="contained" color='secondary' fullWidth onClick={handleDelete}>delete</Button></div> */}
        </div>
    );
}
