import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import ResultValue from './resultvalue';
import SampleImage from '../../static/sample_image.jpg';
import Files from '../../static/files.json';
import CloseIcon from '@material-ui/icons/Close';

const files = [
    { id: 1, name: "file1.pdf", pages: 8, processing_date: "2020-11-12 16:06:59", uploader: "dev123@adapt.com", link: '/detail1' },
    { id: 2, name: "file2.pdf", pages: 3, processing_date: "2020-11-13 18:07:04", uploader: "dev123@adapt.com", link: '/detail2' },
    { id: 3, name: "file3.pdf", pages: 2, processing_date: "2020-11-11 08:13:52", uploader: "dev123@adapt.com", link: '/detail3' },
    { id: 4, name: "file4.pdf", pages: 1, processing_date: "2020-12-12 13:22:34", uploader: "dev123@adapt.com", link: '/detail4' },
    { id: 5, name: "file5.pdf", pages: 3, processing_date: "2020-05-22 17:04:22", uploader: "dev123@adapt.com", link: '/detail5' },
];


const useStyles = makeStyles((theme) => ({
    box: {
        padding: theme.spacing(1),
        color: theme.palette.text.secondary,
        whiteSpace: 'wrap',
        margin: theme.spacing(2),
        textAlign: 'left'
    },
    table: {
        backgroundColor: 'rgba(255,255, 255, 0.3)',
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
        whiteSpace: 'wrap',
        margin: theme.spacing(2),
    },
    controlBar: {
        display:'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        border:'solid'
    }
}));


export default function Detail({ fileIndex }) {
    const classes = useStyles();

    return (
        <Container id='detail'>
            <Grid container justify="center" alignItems="stretch">
                <Grid item xs={12} >
                    <Box bgcolor="primary.light" className={classes.box} height={50}>
                        <div className={classes.controlBar}>{Files[fileIndex].name}</div>
                    </Box>
                </Grid>
                <Grid item xs={5}>
                    <Box bgcolor="primary.light" className={classes.table} height={650}>
                        <ResultValue fileIndex={fileIndex}/>
                    </Box>
                </Grid>
                <Grid item xs={7} >
                    <Box bgcolor="primary.light" className={classes.box} height={650}>
                        <div style={{ height: '650px', overflow: 'auto' }}>
                            <img src={SampleImage} alt="sample_image" style={{ width: '100%' }} />
                        </div>
                    </Box>
                </Grid>
                <Grid item xs={12}>
                    <Box bgcolor="primary.light" id='doc_info' className={classes.box} height={100}>doc info</Box>
                </Grid>
            </Grid>
        </Container>
    );
}
