import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Logo from '../main_ver2/logo';
import TextField from '@material-ui/core/TextField';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import InputAdornment from '@material-ui/core/InputAdornment';
import login from '../../static/login.png';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    root: {
        textAlign: 'left',
    },
    div: {
        backgroundColor: 'rgba(107, 186, 167, 0.5)',
        height: '70%',
    },
    container: {
        padding: 0,
        height: '100%',
    },
    gridContainer: {
        marginTop: theme.spacing(4),
        height: '100%',
    },
    card: {
        backgroundColor: 'rgba(255,255,255,0.4)',
        height: '80%',
        marginTop: theme.spacing(6),
        borderRadius: '4px',
        width: '60%',
        boxShadow: '1px 1px 4px grey',
        padding: theme.spacing(2)
    },
    formControl: {
        width: '100%',
        marginTop: theme.spacing(7),
    },
    input: {
        marginTop: theme.spacing(1),
    },
    btn: {
        backgroundColor: 'rgba(251,161,0, 0.8)',
        marginTop: theme.spacing(3),
        color:'#555'
    },
}));

const handleLogin = () => {
    console.log("login");
}

export default function Login() {
    const classes = useStyles();

    return (
        <Box height={900} className={classes.root}>
            <Logo />
            <div className={classes.div}>
                <Container className={classes.container}>
                    <Grid container direction="row" justify="center" alignItems="stretch" className={classes.gridContainer}>
                        <Grid item xs={7} className={classes.gridItem}>
                            {/* <img src={login} width='50%' /> */}
                        </Grid>

                        <Grid item xs={5} className={classes.gridItem}>
                            <div className={classes.card}>
                                <div style={{ fontSize: '50px', color: '#fff' }}>Login</div>
                                <div id='login_card_txt' style={{ fontSize: '10px', color: '#999', borderBottom: '1px solid #999', paddingBottom: '8px' }}>Welcome. Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum is simply dummy text of the printing and typesetting industry.</div>

                                <FormControl id='auth' fullWidth='true' className={classes.formControl} variant="outlined">
                                    <TextField className={classes.input} id="auth-email" label="Email" variant="outlined" />
                                    <TextField className={classes.input} id="auth-password" label="Password" type="password" autoComplete="current-password" variant="outlined" />
                                </FormControl>
                                <InputLabel htmlFor='auth'>
                                    <Button variant="contained" fullWidth='true' disableElevation='true' onChange={handleLogin} className={classes.btn}>login</Button>
                                </InputLabel>

                            </div>
                        </Grid>
                    </Grid>
                </Container>
            </div>
        </Box>
    );
}
