import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: theme.spacing(3)
    },
    btn: {
        backgroundColor: 'rgba(251,161,0, 0.5)',
        width: '30%',
        display: 'block',
        margin: 'auto',
        padding: theme.spacing(1),
        borderRadius: '500px',
    }
}));

