import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import ResultValue from './resultvalue';
import SampleImage from '../../static/sample_image.jpg';
import Files from '../../static/files.json';
import CloseIcon from '@material-ui/icons/Close';
import { Link } from "react-router-dom";
import IconButton from '@material-ui/core/IconButton';


const useStyles = makeStyles((theme) => ({
    box: {
        padding: theme.spacing(1),
        color: theme.palette.text.secondary,
        whiteSpace: 'wrap',
        margin: theme.spacing(2),
        textAlign: 'left'
    },
    table: {
        backgroundColor: 'rgba(255,255, 255, 0.3)',
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
        whiteSpace: 'wrap',
        margin: theme.spacing(2),
    },
    controlBar: {
        display:'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        fontSize: '50px',
    }
}));


export default function Detail({ fileIndex }) {
    const classes = useStyles();

    return (
        <Container id='detail'>
            <Grid container justify="center" alignItems="stretch">
                <Grid item xs={12} >
                    <Box bgcolor="primary.light" className={classes.box} height={50}>
                        <div className={classes.controlBar}>
                            {Files[fileIndex].name}
                            <Link to='/' style={{textDecoration:'none'}}>
                            <IconButton aria-label="delete">
                                <CloseIcon fontSize="large" color='primary.light'/>
                                </IconButton>
                            </Link>
                        </div>
                    </Box>
                </Grid>
                <Grid item xs={5}>
                    <Box bgcolor="primary.light" className={classes.table} height={650}>
                        <ResultValue fileIndex={fileIndex}/>
                    </Box>
                </Grid>
                <Grid item xs={7} >
                    <Box bgcolor="primary.light" className={classes.box} height={650}>
                        <div style={{ height: '650px', overflow: 'auto' }}>
                            <img src={SampleImage} alt="sample_image" style={{ width: '100%' }} />
                        </div>
                    </Box>
                </Grid>
                <Grid item xs={12}>
                    <Box bgcolor="primary.light" id='doc_info' className={classes.box} height={100}>doc info</Box>
                </Grid>
            </Grid>
        </Container>
    );
}
