import { Route, BrowserRouter as Router, Switch } from "react-router-dom";
import React from 'react';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import "./styles/App.css";
import Main from "./components/main/main";
import Main2 from "./components/main_ver2/main_ver2";
import Files from './static/samplefile.json';
import Detail from "./components/detail_ver2/detail_ver2";
import Login from "./components/login/login";



function App() {
  const font = "'Open Sans Condensed', 'sans-serif'";
  const theme = createMuiTheme({
    palette: {
      primary: {
        light: '#3b3b58',
        main: '#33334c',
      },
      secondary: {
        main: '#f5f5f5',
        light: '#fba100',
      },
    },
    typography: {
      fontFamily: font,
    },
    overrides: {
      MuiOutlinedInput: {
        root: {
          backgroundColor: 'rgba(255,255,255,0.5)',
          '& fieldset': {
            borderColor: "",
          },
          '&:hover fieldset': {
            borderColor: '',
          },
          '&.Mui-focused fieldset': {
            borderColor: '',
          },
        }
      }
    },
  });

  const getFiles = Files.files;
  // const [user, setUser] = React.useState(null);
  // const authenticated = user != null;

  // const login = ({ email, password }) => setUser(Authentication({ email, password }));
  // const logout = () => setUser(null);

  const renderDetail = getFiles.map((file) =>
    <Route
      key={file.id}
      path={`/${parseInt(file.id)}`}
      exact
      render={() => <Detail fileId={parseInt(file.id)} />}
    >
    </Route>
  );

  return (
    <div className="App">
      <ThemeProvider theme={theme}>
        <Router>
          <Switch>
            <Route path='/' exact component={Login}></Route>
            <Route path='/main' exact component={Main2}></Route>
            <Route path='/ver1' exact component={Main}></Route>
            {renderDetail}
          </Switch>
        </Router>
      </ThemeProvider>
    </div>
  );
}

export default App;
