import { Route, BrowserRouter as Router, Switch } from "react-router-dom";
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import "./styles/App.css";
import Main from "./components/main/main";
import Detail from "./components/detail/detail";
import { deepPurple } from '@material-ui/core/colors';

const files = [
  { id: 1, name: "file1.pdf", pages: 8, processing_date: "2020-11-12 16:06:59", uploader: "dev123@adapt.com", link: '/detail1' },
  { id: 2,  name: "file2.pdf", pages: 3, processing_date: "2020-11-13 18:07:04", uploader: "dev123@adapt.com", link: '/detail2' },
  { id: 3, name: "file3.pdf", pages: 2, processing_date: "2020-11-11 08:13:52", uploader: "dev123@adapt.com", link: '/detail3' },
  { id: 4, name: "file4.pdf", pages: 1, processing_date: "2020-12-12 13:22:34", uploader: "dev123@adapt.com", link: '/detail4' },
  { id: 5, name: "file5.pdf", pages: 3, processing_date: "2020-05-22 17:04:22", uploader: "dev123@adapt.com", link: '/detail5' },
];


function App() {
  const font = "'Open Sans Condensed', 'sans-serif'";
  const theme = createMuiTheme({
    palette: {
      primary: {
        light: '#3b3b58',
        main: '#33334c',
      },
      secondary: {
        main: '#483698'
      }
    },
    typography: {
      fontFamily: font,
    },
    overrides: {
      MuiOutlinedInput: {
        root: {
          backgroundColor: 'rgba(255,255,255,0)',
          color: deepPurple[50],
          '&:hover fieldset': {
            // backgroundColor: '#33334c',
            // borderColor: deepPurple[300]
          },
          '&.Mui-focused feldset': {
            // backgroundColor: '#33334c',
            // borderColor: deepPurple[300]
          }
        }
      }
    }
  });

  const renderDetail = files.map((file) => 
   <Route path={file.link} exact render={() => <Detail fileIndex={file.id}/>}></Route>
  );

  return (
    <div className="App">
      <ThemeProvider theme={theme}>
        <Router>
          <Switch>
            <Route path='/' exact component={Main}></Route>
            <Route path='/detail' exact component={Detail}></Route>
          </Switch>
        </Router>
      </ThemeProvider>
    </div>
  );
}

export default App;
